from langflow.custom import Component
from langflow.inputs import MessageTextInput
from langflow.io import Output
from langflow.schema import Data

class SQLTemplateConverterComponent(Component):
    display_name = "SQL Template Converter Jinja2"
    description = "Wraps a plain SQL snippet into a Jinja2 SQL template using the PostgreSQL dialect macros."
    documentation = "https://docs.langflow.org/components/custom"
    icon = "code"

    inputs = [
        MessageTextInput(
            name="sql_template",
            display_name="SQL Template",
            info="Basic SQL template snippet to be embedded (e.g., SELECT column1, column2).",
            tool_mode=True,
        ),
    ]

    outputs = [
        Output(
            display_name="Jinja2 SQL Template",
            name="result",
            type_=Data,
            method="convert_template",
        ),
    ]

    def convert_template(self) -> Data:
        jinja2_template = f"""{{% import '/dialects/postgresql.sql.jinja2' as lib with context -%}}
{self.sql_template}
{{{{ lib.render_data_grouping_projections('analyzed_table') }}}}
{{{{ lib.render_time_dimension_projection('analyzed_table') }}}}
FROM {{{{ lib.render_target_table() }}}} AS analyzed_table
{{{{ lib.render_where_clause() }}}}
{{{{ lib.render_group_by() }}}}
{{{{ lib.render_order_by() }}}}
"""

        return Data(data={"jinja2_sql_template": jinja2_template})

    def build(self):
        return self.convert_template

import requests
from langflow.custom import Component
from langflow.inputs import MessageTextInput
from langflow.io import Output
from langflow.schema import Data

class RunChecksJobComponent(Component):
    display_name = "Run Checks Job"
    description = "Triggers DQOps column-level profiling check execution job."
    documentation = "https://docs.langflow.org/components/custom"
    icon = "play-circle"

    inputs = [
        MessageTextInput(
            name="category",
            display_name="Category",
            info="Check category used to construct the check name path.",
            tool_mode=True,
        ),
        MessageTextInput(
            name="check_name",
            display_name="Check Name",
            info="The name of the check to run.",
            tool_mode=True,
        ),
        MessageTextInput(
            name="base_url",
            display_name="Base Url",
            info="Url for data quality",
            tool_mode=False,
            required=True,
        ),
    ]

    outputs = [
        Output(
            display_name="Result",
            name="result",
            type_=Data,
            method="run_checks",
        ),
    ]

    def run_checks(self) -> Data:
        url = f"{self.base_url}/api/jobs/runchecks?wait=false"

        headers = {
            "Content-Type": "application/json",
        }

        body = {
            "connection": "postgres",
            "checkName": f"column/profiling/{self.category}/{self.check_name}",
            "fullTableName": "*.*",
            "enabled": True,
            "checkTarget": "column",
            "checkType": "profiling",
        }

        try:
            response = requests.post(url, json=body, headers=headers, timeout=30)
            if response.status_code != 201:
                return Data(data={
                    "success": False,
                    "status_code": response.status_code,
                    "error": response.text,
                })

            return Data(data={
                "success": True,
                "status_code": response.status_code,
                "job_response": response.json(),
                "check_run": body
            })

        except requests.RequestException as e:
            return Data(data={
                "success": False,
                "error": str(e)
            })

    def build(self):
        return self.run_checks

import requests
from langflow.custom import Component
from langflow.inputs import MessageTextInput
from langflow.io import Output
from langflow.schema import Data

class SearchColumnsComponent(Component):
    display_name = "Fetch schema"
    description = "Fetches column info from an API using a connection name and returns table name, column name, and column type."
    documentation = "https://docs.langflow.org/components/custom"
    icon = "columns"

    inputs = [
        MessageTextInput(
            name="connection_name",
            display_name="Connection Name",
            info="The database connection name to be used in the request.",
            tool_mode=True,
        ),
        MessageTextInput(
            name="base_url",
            display_name="Base Url",
            info="Url for data quality",
            tool_mode=False,
            required=True,
        ),
    ]

    outputs = [
        Output(
            display_name="Columns Info",
            name="result",
            type_=Data,
            method="search_columns",
        ),
    ]

    def search_columns(self) -> Data:
        base_url = f"{self.base_url}/api/search/columns"
        params = {
            "connection": self.connection_name,
            "schema": "",
            "table": "",
            "column": ""
        }

        headers = {
            "Accept-Encoding": "identity"
        }

        try:
            response = requests.get(base_url, params=params, headers=headers, timeout=10)
            if response.status_code != 200:
                return Data(data={
                    "valid": False,
                    "status_code": response.status_code,
                    "error": "Request failed"
                })

            columns_raw = response.json()
            simplified_columns = []

            for col in columns_raw:
                simplified_columns.append({
                    "table_name": col.get("table", {}).get("table_name"),
                    "column_name": col.get("column_name"),
                    "column_type": col.get("type_snapshot", {}).get("column_type"),
                })

            return Data(data={
                "valid": True,
                "columns": simplified_columns
            })

        except requests.RequestException as e:
            return Data(data={
                "valid": False,
                "error": str(e)
            })

    def build(self):
        return self.search_columns
